<style>
	main {
		height: max-content;
		background-color: black;
		color: white;
		display: flex;
		justify-content: center;
		padding-top: 3rem;
		padding-bottom: 7rem;
	}

	h1 {
		margin: 0;
		font-size: 4rem;
		width: 90vw;
	}

	p {
		font-size: 1.8rem;
	}

	.answer {
		margin-block-start: 0em;
		margin-block-end: 0em;
		font-size: 1.1rem;
	}

	.lead {
		width: 90vw;
		line-height: 3.5rem;
		font-size: 1.8rem;
	}

	.grid {
		display: grid;
		grid-template-columns: 30vw 30vw 30vw;
		grid-template-rows: auto;
		gap: 24px 24px;
		margin-bottom: 72px;
		margin-top: 48px;
	}

	.box {
		display: flex;
		flex-direction: column;
		gap: 8px;
		border: 2px solid var(--primary-green);
		padding: 1rem;
	}

	h2 {
		font-size: 2.5rem;
		margin: 0;
		margin-block-end: 0em;
	}

	.m-hidden {
		display: flex;
	}

	.m-shown {
		display: none !important;
	}

	h3 {
		margin-block-start: 0em;
		margin-block-end: 0em;
		font-size: 1.3em;
	}

	@media (max-width: 850px) {
		.grid {
			grid-template-columns: 90vw;
		}
		.m-hidden {
			display: none !important;
		}
		.m-shown {
			display: flex;
		}
	}
</style>
